// src/App.js

import React, { useState } from "react";
import "./App.css";
import JobList from "./JobList.js";
import JobDetails from "./JobDetails.js";

function App() {
    const [showNextSteps, setShowNextSteps] = useState(false);

    const handleApplyNow = () => {
        setShowNextSteps(true);
    };

    // Define state variables for the selected job and the match percentage
    const [selectedJob, setSelectedJob] = useState(null);
    const [match, setMatch] = useState(0);

    // Define a function to handle job selection
    const handleJobSelect = (job) => {
        setSelectedJob(job); // Update state variable with selected job
        setMatch(Math.floor(Math.random() * 100) + 1); // Update state variable with random match percentage
    };

    // Define some dummy jobs as an array of objects
    const jobs = [
        {
            id: 1,
            title: "Web Developer",
            company: "ABC Inc.",
            location: "New York, NY",
            salary: 80000,
        },
        {
            id: 2,
            title: "Graphic Designer",
            company: "XYZ Ltd.",
            location: "Los Angeles, CA",
            salary: 60000,
        },
        {
            id: 3,
            title: "Data Analyst",
            company: "123 Corp.",
            location: "Chicago, IL",
            salary: 70000,
        },
    ];

    return (
        <div className="App">
            <header>
                <h1>Welcome to the Job Application Disruption System</h1>
            </header>
            <main>
                <section>
                    <h2>Apply to Recommended Jobs</h2>
                    <p>Apply to up to 10 recommended jobs per day with just one click.</p>
                    <button onClick={handleApplyNow}>Apply Now</button>
                </section>
                {showNextSteps && <JobList jobs={jobs} onSelect={handleJobSelect} />}
                {selectedJob && <JobDetails job={selectedJob} match={match} />}
                <section>
                    <h2>Generate Personalized Application Materials</h2>
                    <p>Let our AI create tailored cover letters, resumes, and portfolios for each job.</p>
                    <button>Generate Materials</button>
                </section>
                <section>
                    <h2>Find Your Perfect Match</h2>
                    <p>Discover the best companies and roles based on your skills and preferences.</p>
                    <button>Find Matches</button>
                </section>
                <section>
                    <h2>Get Instant Feedback and Guidance</h2>
                    <p>Receive real-time feedback on your applications, interviews, and offers.</p>
                    <button>Get Feedback</button>
                </section>
            </main>
            <footer>
                <p>Job Application Disruption System Â© 2023</p>
            </footer>
        </div>
    );
}

export default App;
